//SPDX-License-Identifier: MIT
pragma solidity =0.7.6;

import "./NimbusToken.sol";

contract Cloudswap {
    string public name = "Clouds Kingdom Swap";
    NimbusToken public nimbus;
    uint256 public rate = 100;

    event BuyNimbus(
        address account,
        address token,
        uint256 amount,
        uint256 rate
    );
    event NimbusSold(
        address account,
        address token,
        uint256 amount,
        uint256 rate
    );

    constructor(NimbusToken _nimbus) {
        nimbus = _nimbus;
    }

    function buyNimbus() public payable returns (bool succress) {
        // value of Avax * Redemption rate
        uint256 nimbusAmount = msg.value * rate;
        require(
            nimbus.balanceOf(address(this)) >= nimbusAmount,
            "Not enough token for swap"
        );
        nimbus.transfer(msg.sender, nimbusAmount);

        emit BuyNimbus(msg.sender, address(nimbus), nimbusAmount, rate);

        return true;
    }

    function sellNimbus(uint256 _amount) public returns (bool sccress) {
        // calculate the amount of Avax to redeem
        uint256 etherAmount = _amount / rate;
        //perform sale
        nimbus.transferFrom(msg.sender, address(this), _amount);
        payable(msg.sender).transfer(etherAmount);
        emit NimbusSold(msg.sender, address(nimbus), etherAmount, rate);
        return true;
    }
}
